package com.example.lab_2

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.lab_2.databinding.FragmentMainBinding

class MainFragment : Fragment(), OnAnimalClickListener {

    private var _binding: FragmentMainBinding? = null
    private val binding get() = _binding!!

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = FragmentMainBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        binding.recyclerView.layoutManager = LinearLayoutManager(requireContext())

        val animalList = listOf(
            Animal(name = "Kangaroo", latin = "Macropus", description = "The kangaroo is a marsupial mammal native to Australia. It is known for its powerful hind legs and ability to hop long distances.", url = "https://upload.wikimedia.org/wikipedia/commons/thumb/9/9e/Macropus_giganteus_-_Brunkerville.jpg/800px-Macropus_giganteus_-_Brunkerville.jpg"),
            Animal(name = "Gorilla", latin = "Gorilla gorilla", description = "The gorilla is a large primate found in the forests of central and eastern Africa. It is known for its strength and gentle nature.", url = "https://files.worldwildlife.org/wwfcmsprod/images/HERO_Mountain_Gorilla_Silverback_WW22557/hero_small/17l7fosr27_Mountain_Gorilla_Silverback_WW22557.jpg"),
            Animal(name = "Cheetah", latin = "Acinonyx jubatus", description = "The cheetah is a fast-running big cat. It has a slender body, spotted coat, and is known for its incredible speed and agility.", url = "data:image/jpeg;base64,"),
            Animal(name = "Panda", latin = "Ailuropoda melanoleuca", description = "The panda is a bear native to China. It has distinctive black and white fur and primarily feeds on bamboo.", url = "https://images.newscientist.com/wp-content/uploads/2023/03/16182802/SEI_148545447.jpg"),
            Animal(name = "Humpback Whale", latin = "Megaptera novaeangliae", description = "The humpback whale is a large marine mammal known for its long migrations and acrobatic displays such as breaching and slapping the water with its fins.", url = "data:image/jpeg;base64,"),
            Animal(name = "Eagle", latin = "Aquila chrysaetos", description = "The eagle is a large bird of prey found in various parts of the world. It has keen eyesight and powerful talons for hunting.", url = "data:image/jpeg;base64,")
        )

        val adapter = AnimalAdapter(animalList, this)

        binding.recyclerView.adapter = adapter
    }

    override fun onClick(animal: Animal) {
        findNavController().navigate(
            MainFragmentDirections.actionMainFragmentToDetailsFragment(animal)
        )
    }
}

interface OnAnimalClickListener {
    fun onClick(animal: Animal)
}